LBUF_OFFSET mux_string::export_Char_UTF8(size_t iFirst, UTF8 *pBuffer) const
{
    if (m_iLast.m_byte <= iFirst)
    {
        if (NULL != pBuffer)
        {
            pBuffer[0] = '\0';
        }
        return 0;
    }

    LBUF_OFFSET nBytes = utf8_FirstByte[m_autf[iFirst]];

    if (UTF8_CONTINUE <= iFirst)
    {
        if (NULL != pBuffer)
        {
            pBuffer[0] = '\0';
        }
        return 0;
    }

    if (NULL != pBuffer)
    {
        LBUF_OFFSET i;
        for (i = 0; i < nBytes; i++)
        {
            pBuffer[i] = m_autf[iFirst + i];
        }
        pBuffer[i] = '\0';
    }
    return nBytes;
}

