void mux_string::import(dbref num)
{
    realloc_m_pcs(0);

    m_autf[0] = '#';
    LBUF_OFFSET n = 1;

    // mux_ltoa() sets the '\0'.
    //
    n = static_cast<LBUF_OFFSET>(n + mux_ltoa(num, m_autf + 1));
    m_iLast(n, n);
}

